fn main() {
    let input = "";
    let list = input.split("");

    let mut potions = 0;

    for creature in list {
        if creature == "\n" {
            continue;
        }

        let mut potions_needed = 0;

        if creature == "A" {
            potions_needed = 0
        }
        if creature == "B" {
            potions_needed = 1
        }
        if creature == "C" {
            potions_needed = 3
        }

        potions += potions_needed
    }

    println!("Part 1: {potions}")
}
